import {useState} from "react";
import axios from "axios";
import {api} from "../api";
import {Navigate, useNavigate} from "react-router-dom";
import {useAuth} from "../Auth/Authenticator";

export default function Login() {
    const [MEMBER_ID, setMEMBER_ID] = useState("");
    const [MEMBER_PWD, setMEMBER_PWD] = useState("");
    const { login } = useAuth();
    const navigate = useNavigate();

    async function onSubmit(e){
        e.preventDefault();
        try{
            await login(MEMBER_ID, MEMBER_PWD);
            alert("로그인 성공");
            navigate("/");
        }catch(err){
            alert("로그인 실패");
            console.error(err?.response?.data || err.message);
        }
    }

    // const ToJoin = () => {
    //     navigate("/Member/Join");
    // };
    //
    // const [isLogin, setIsLogin] = useState(true);
    //
    // const toggleForm = () => setIsLogin(!isLogin);


    const [isLogin, setIsLogin] = useState(true);
    const [isAnimating, setIsAnimating] = useState(false);

    const changeForm = () => {
        setIsAnimating(true);
        setTimeout(() => setIsAnimating(false), 1500);
        setIsLogin(!isLogin);
    };
    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-200 font-sans">
            <div className="relative w-[1000px] h-[600px] p-6 rounded-xl bg-gray-100 shadow-[10px_10px_20px_rgba(0,0,0,0.1),-10px_-10px_20px_rgba(255,255,255,0.8)] overflow-hidden">

                {/* 왼쪽 컨테이너 */}
                <div
                    className={`absolute top-0 left-0 w-1/2 h-full flex flex-col items-center justify-center p-8 transition-all duration-700 ${
                        isLogin ? "translate-x-0 opacity-100" : "-translate-x-full opacity-0"
