import {useState} from "react";
import axios from "axios";
import {api} from "../api";
import {Navigate, useNavigate} from "react-router-dom";
import {useAuth} from "../Auth/Authenticator";

export default function Login() {
    const [MEMBER_ID, setMEMBER_ID] = useState("");
    const [MEMBER_PWD, setMEMBER_PWD] = useState("");
    const { login } = useAuth();
    const navigate = useNavigate();

    async function onSubmit(e){
        e.preventDefault();
        try{
            await login(MEMBER_ID, MEMBER_PWD);
            alert("로그인 성공");
            navigate("/");
        }catch(err){
            alert("로그인 실패");
            console.error(err?.response?.data || err.message);
        }
    }

    // const ToJoin = () => {
    //     navigate("/Member/Join");
    // };
    //

        const [isSignUp, setIsSignUp] = useState(false);
        const [isAnimating, setIsAnimating] = useState(false);

        const handleToggle = (e) => {
            e.preventDefault();
            setIsAnimating(true);
            setTimeout(() => {
                setIsAnimating(false);
            }, 1250);
            setIsSignUp(!isSignUp);
        };

        const handleSubmit = (e) => {
            e.preventDefault();
            // Handle form submission here
        };
    return (
        <div className="w-full flex justify-center items-center p-8 font-['Inter'] text-sm text-gray-500">
            <div className="relative w-[893px] h-[613px] bg-neutral-100 rounded-3xl overflow-hidden shadow-neu">

                {/* Sign In Container (Left Panel) */}
                <div className={`absolute top-0 w-[480px] h-full p-8 bg-neutral-100 rounded-3xl transition-all duration-[1250ms] shadow-neu ${
                    isSignUp ? 'left-[413px] z-10' : 'left-0 z-50'
                }`}>
                    <div className="flex flex-col h-full">
                        <h2 className="text-2xl font-semibold text-black leading-loose mb-12">Join</h2>

                        {/* Email Field */}
                        <div className="mb-6">
                            <label className="block text-black text-sm font-medium leading-tight mb-4">Email</label>
                            <div className="w-full h-12 neu-input rounded-2xl flex items-center">
                                <input
                                    type="email"
                                    placeholder="you@comma.app"
                                    className="w-full h-full px-4 bg-transparent text-neutral-400 text-base outline-none rounded-2xl"
                                />
                            </div>
                        </div>

                        {/* Password Field */}
                        <div className="mb-8">
                            <div className="flex justify-between items-center mb-4">
                                <label className="text-black text-sm font-medium leading-tight">Password</label>
                                <span className="text-gray-500 text-sm font-normal leading-tight cursor-pointer hover:text-black transition-colors">Forgot?</span>
                            </div>
                            <div className="w-full h-12 neu-input rounded-2xl flex items-center">
                                <input
                                    type="password"
                                    placeholder="••••••••"
                                    className="w-full h-full px-4 bg-transparent text-neutral-400 text-base outline-none rounded-2xl"
                                />
                            </div>
                        </div>

                        {/* Remember Me */}
                        <div className="flex items-center mb-8">
                            <div className="w-16 h-8 bg-neutral-100 rounded-full shadow-neu flex items-center px-1">
                                <div className="w-6 h-6 bg-gray-200 rounded-full shadow-neu-sm"></div>
                            </div>
                            <span className="ml-4 text-gray-500 text-sm font-normal leading-tight">Remember me</span>
                        </div>

                        {/* Sign In Button */}
                        <button
                            onClick={handleSubmit}
                            className="w-full h-11 rounded-lg border border-black bg-neutral-100 shadow-[0px_1px_2px_rgba(0,0,0,0.05)] neu-button mb-8"
                        >
                            <span className="text-black text-sm font-semibold leading-none">Sign in</span>
                        </button>

                        {/* OR Divider */}
                        <div className="text-center mb-6">
                            <span className="text-gray-500 text-xs font-normal uppercase leading-none tracking-wide">OR</span>
                        </div>

                        {/* Social Login Buttons */}
                        <div className="flex gap-4 justify-center">
                            <button className="w-24 h-11 bg-neutral-100 rounded-2xl shadow-neu neu-button flex items-center justify-center">
                                <div className="w-5 h-5 bg-slate-800 rounded"></div>
                            </button>

                            <button className="w-24 h-11 bg-neutral-100 rounded-2xl shadow-neu neu-button flex items-center justify-center">
                                <div className="w-5 h-5 relative">
                                    <div className="w-2 h-2 absolute right-0 bottom-0 bg-red-500 rounded-full"></div>
                                    <div className="w-1 h-2.5 absolute left-0 top-1 bg-green-600 rounded"></div>
                                    <div className="w-3.5 h-1.5 absolute bottom-0 left-1 bg-blue-500 rounded"></div>
                                    <div className="w-3.5 h-2 absolute top-0 right-1 bg-yellow-500 rounded"></div>
                                </div>
                            </button>

                            <button className="w-24 h-11 bg-neutral-100 rounded-2xl shadow-neu neu-button flex items-center justify-center">
                                <div className="w-5 h-4 bg-slate-800 rounded"></div>
                            </button>
                        </div>
                    </div>
                </div>

                {/* Sign Up Container (Right Panel) */}
                <div className={`absolute top-0 w-[480px] h-full p-8 bg-neutral-100 rounded-3xl transition-all duration-[1250ms] shadow-neu ${
                    isSignUp ? 'left-0 z-50' : 'left-[413px] z-10'
                }`}>
                    <div className="flex flex-col h-full">
                        <h2 className="text-3xl font-semibold text-black leading-loose mb-8">Create Account</h2>

                        {/* Name Field */}
                        <div className="mb-6">
                            <label className="block text-black text-sm font-medium leading-tight mb-4">Full Name</label>
                            <div className="w-full h-12 neu-input rounded-2xl flex items-center">
                                <input
                                    type="text"
                                    placeholder="Enter your name"
                                    className="w-full h-full px-4 bg-transparent text-neutral-400 text-base outline-none rounded-2xl"
                                />
                            </div>
                        </div>

                        {/* Email Field */}
                        <div className="mb-6">
                            <label className="block text-black text-sm font-medium leading-tight mb-4">Email</label>
                            <div className="w-full h-12 neu-input rounded-2xl flex items-center">
                                <input
                                    type="email"
                                    placeholder="you@comma.app"
                                    className="w-full h-full px-4 bg-transparent text-neutral-400 text-base outline-none rounded-2xl"
                                />
                            </div>
                        </div>

                        {/* Password Field */}
                        <div className="mb-8">
                            <label className="block text-black text-sm font-medium leading-tight mb-4">Password</label>
                            <div className="w-full h-12 neu-input rounded-2xl flex items-center">
                                <input
                                    type="password"
                                    placeholder="••••••••"
