// src/api.js (새 파일 추천)
import axios from "axios";
export const api = axios.create({
    baseURL: "/api",              // ★ setupProxy의 '/api'와 일치
    withCredentials: true,        // (시큐리티 껐으면 없어도 OK)
    headers: { "Content-Type": "application/json" },
});
