import { useEffect, useState } from "react";

export default function ThemeToggle() {
    const [mounted, setMounted] = useState(false);
    const [theme, setTheme] = useState(() => {
        if (typeof window === "undefined") return "light";
        const saved = localStorage.getItem("theme");
        if (saved === "dark" || saved === "light") return saved;
        const prefersDark = window.matchMedia("(prefers-color-scheme: dark)").matches;
        return prefersDark ? "dark" : "light";
    });
    useEffect(() => setMounted(true), []);
    useEffect(() => {
        if (!mounted) return;
        const root = document.documentElement;
        if (theme === "dark") root.classList.add("dark");
        else root.classList.remove("dark");
        localStorage.setItem("theme", theme);
    }, [theme, mounted]);
    return (
        <button
            aria-label="Toggle theme"
            onClick={() => setTheme((t) => (t === "dark" ? "light" : "dark"))}
            className="relative inline-flex h-9 w-16 items-center rounded-full bg-background shadow-neo transition-all active:shadow-neo-inset"
        >
      <span
          className={`absolute left-1 top-1 inline-flex h-7 w-7 items-center justify-center rounded-full bg-secondary text-foreground shadow-neo transition-all ${theme === "dark" ? "translate-x-7" : "translate-x-0"}`}
      >
        {theme === "dark" ? (
            <svg viewBox="0 0 24 24" className="h-4 w-4" fill="currentColor" aria-hidden>
                <path d="M21.64 13a9 9 0 11-10.63-10.6 7 7 0 1010.63 10.6z" />
            </svg>
        ) : (
            <svg viewBox="0 0 24 24" className="h-4 w-4" fill="currentColor" aria-hidden>
                <path d="M12 4.5a1 1 0 011 1v1.75a1 1 0 01-2 0V5.5a1 1 0 011-1zm0 12.25a1 1 0 011 1v1.75a1 1 0 01-2 0v-1.75a1 1 0 011-1zM6.22 6.22a1 1 0 011.42 0l1.24 1.24a1 1 0 01-1.42 1.42L6.22 7.64a1 1 0 010-1.42zm9.9 9.9a1 1 0 011.42 0l1.24 1.24a1 1 0 01-1.42 1.42l-1.24-1.24a1 1 0 010-1.42zM4.5 12a1 1 0 011-1h1.75a1 1 0 010 2H5.5a1 1 0 01-1-1zm12.25 0a1 1 0 011-1h1.75a1 1 0 010 2h-1.75a1 1 0 01-1-1zM6.22 17.78a1 1 0 010-1.42l1.24-1.24a1 1 0 011.42 1.42l-1.24 1.24a1 1 0 01-1.42 0zm9.9-9.9a1 1 0 010-1.42l1.24-1.24a1 1 0 011.42 1.42L17.54 7.3a1 1 0 01-1.42 0z" />
            </svg>
        )}
      </span>
        </button>
    );
}
