function Join() {
    // 상태 변수 선언
    const [MEMBER_ID, setMEMBER_ID] = useState(""); // 회원 이메일
    const [MEMBER_PWD, setMEMBER_PWD] = useState(""); // 회원 비밀번호
    const [MEMBER_PWD_CHECK, setMEMBER_PWD_CHECK] = useState(""); // 비밀번호 확인

    const navigate = useNavigate(); // 페이지 이동을 위한 훅

    // 회원가입 처리 함수
    const handleJoin = async (e) => {
        e.preventDefault(); // 폼 기본 제출 동작 방지

        try {
            // 비밀번호 일치 여부 확인
            if (MEMBER_PWD !== MEMBER_PWD_CHECK) {
                alert("비밀번호가 일치하지 않습니다.");
                return;
            }

            console.log("회원가입 요청 시작");

            // 서버에 회원가입 요청
            const res = await axios.post("/member/join/new", {
                memberId: MEMBER_ID,
                memberPwd: MEMBER_PWD
            });

            console.log("회원가입 응답 받음");

            // 성공 시 처리
            if (res.data.status === "OK") {
                alert("회원가입 성공");
                navigate("/"); // 메인 페이지로 이동
            }
        } catch (err) {
            console.error("회원가입 에러:", err);
            alert("회원가입 실패");
        }
    };

    return(
        <div>
            <h3>회원가입</h3>
            <form onSubmit={handleJoin}>
                <label>이메일</label>
                <input type={"email"} value={MEMBER_ID} onchange={(e) => setMEMBER_ID(e.target.value)} placeholder={"Email"} />


                <label>비밀번호</label>
                <input type={"password"} value={MEMBER_PWD} onChange={(e) => setMEMBER_PWD(e.target.value)} placeholder={"Password"}/>

            <button></button>
            </form>
        </div>
    )
}

export default Join;
