import { forwardRef } from "react";
import { cn } from "@/lib/utils";

const DaisyButton = forwardRef(({ variant = "primary", size = "md", wide, circle, loading, className, disabled, children, ...props }, ref) => {
    const base = "btn";
    const variantClass = variant === "outline" ? "btn-outline" : `btn-${variant}`;
    const sizeClass = size ? `btn-${size}` : "";
    const wideClass = wide ? "btn-wide" : "";
    const circleClass = circle ? "btn-circle" : "";
    const loadingClass = loading ? "loading" : "";

    return (
        <button
            ref={ref}
            className={cn(base, variantClass, sizeClass, wideClass, circleClass, loadingClass, className)}
            disabled={disabled || loading}
            {...props}
        >
            {children}
        </button>
    );
});

DaisyButton.displayName = "DaisyButton";
export default DaisyButton;
