import React, { Component, Fragment } from 'react';
import './App.css';
import Header from "./Components/Header";
import Footer from "./Components/Footer";

import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider } from "./Auth/Authenticator";
import Home from "./Components/Home";
import Login from "./Member/Login";
import Join from "./Member/Join";
import Success from "./Member/Success";

class App extends Component {
    render() {
        return (
            <Router>
                <AuthProvider>
                    <Fragment>
                        <Header />
                        <div>
                            <Routes>
                                {/* Ìôà */}
                                <Route path="/" element={<Home />} />

                                {/* ‚úÖ ÏÜåÎ¨∏Ïûê Í∏∞Ï§Ä ÎùºÏö∞Ìä∏ */}
                                <Route path="/member/login" element={<Login />} />
                                <Route path="/member/join" element={<Join />} />

                                {/* Ìò∏ÌôòÏö©(ÎåÄÎ¨∏ÏûêÎ°ú Ï†ëÍ∑ºÌï¥ÎèÑ ÏÜåÎ¨∏ÏûêÎ°ú Î¶¨Îã§Ïù¥Î†âÌä∏) */}
                                <Route path="/Member/Login" element={<Navigate to="/member/login" replace />} />
                                <Route path="/Member/Join" element={<Navigate to="/member/join" replace />} />
                            </Routes>
                        </div>
                        <Footer />
                    </Fragment>
                </AuthProvider>
            </Router>
        );
    }
}

export default App;

